  position: relative;
  padding: 10vh 2em;
  margin: 2em;
  border-radius: 10px;
  box-shadow: inset 6px 6px 16px rgba(163,177,198,0.6), inset 6px 6px 16px rgba(255,255,255, 0.5);

    h1 {
      margin: 0;
      text-align: center;
    }

    img {
      width: 70px;
      position: absolute;
      bottom: 0;

      &:first-of-type {
        left: 50px;
      }

      &:nth-of-type(2) {
        left: 90px;
      }

      &:nth-of-type(3) {
        right: 90px;
      }
    }
}

.beer-list {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;

  .beer {
    width: calc(25% - 5em);
    margin: 1em;
    padding: 1em;
    background-color: $light-blue-sec;
    border-radius: 10px;
    box-shadow: 3px 3px 16px rgba(163,177,198,0.6), -3px -3px 16px rgba(255,255,255, 0.5);

    img {
      width: calc(100% - 4em);
      padding: 0 2em;
    }

    h2 {
      margin: 20px 0 10px;
    }

    p {
      margin: 0;
    }
  }
}

.search {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  div {
    position: relative;
    width: 350px;
    margin: 1em 0;
    display: flex;
    align-items: flex-end;
  }

  input {
    width: 60%;
    box-sizing: border-box;
    outline: none;
  }

  label {
    position: absolute;
    width: 60%;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: -1;
  }

  label {
    width: 75%;
    overflow: hidden;

    &::after {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      bottom: 0;
      width: 300%;
      transition: transform .7s ease-out;
      background: no-repeat url(data:image/svg+xml;base64,);
      filter: invert(100%);
      -webkit-filter: invert(100%);
    }
  }
  input {
    width: 100%;
    box-sizing: border-box;
    outline: none;
  }
  span {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    transition: opacity .2s ease-in-out .15s;
  }
  input, span {
    padding: .8em .5em;
    margin: 0;
    border: 0;
    font-family: robot, sans-serif, arial;
    font-size: 1em;
    text-align: left;
    color: $black;
    background: none;
  }
  input:focus, input:valid {
    & + label span {
      opacity: 0;
    }
    & + label::after {
      transform: translateX(-66.6%);
    }
  }

  #submit-btn {
    padding: 7px 30px;
    margin-bottom: 1.8px;
    background-color: black;
    color: white;
    font-family: raleway, arial, sans-serif;
    font-size: 1em;
    border: none;
  }
}

